// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.6.12;

import "./X2MStandardToken.sol";

contract CloneFactory {
    function createClone(address target)
        internal
        returns (address payable result)
    {
        bytes20 targetBytes = bytes20(target);
        assembly {
            let clone := mload(0x40)
            mstore(
                clone,
                0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000
            )
            mstore(add(clone, 0x14), targetBytes)
            mstore(
                add(clone, 0x28),
                0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000
            )
            result := create(0, clone, 0x37)
        }
    }

    function isClone(address target, address query)
        internal
        view
        returns (bool result)
    {
        bytes20 targetBytes = bytes20(target);
        assembly {
            let clone := mload(0x40)
            mstore(
                clone,
                0x363d3d373d3d3d363d7300000000000000000000000000000000000000000000
            )
            mstore(add(clone, 0xa), targetBytes)
            mstore(
                add(clone, 0x1e),
                0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000
            )

            let other := add(clone, 0x40)
            extcodecopy(query, other, 0, 0x2d)
            result := and(
                eq(mload(clone), mload(other)),
                eq(mload(add(clone, 0xd)), mload(add(other, 0xd)))
            )
        }
    }
}

contract X2MStandardTokenFactory is Ownable, CloneFactory {
    address payable private masterContract;
    event TokenCreated(address newTokenAddress);
    address newOwner;
    address payable public clone;
    constructor (address payable contractToClone) public {
        masterContract = contractToClone;
    }
    function setMasterContract(address payable contractToClone) public onlyOwner() {
        masterContract = contractToClone;
    }

    function createStandardToken(
        string memory tokenName,
        string memory tokenSymbol,
        uint256 tokenInitialAmount,
        bool isPool,
        address dexAddress
    ) public payable onlyOwner() {
        clone = createClone(masterContract);
        newOwner = _msgSender();
        X2MStandardToken(clone).init(tokenName, tokenSymbol, tokenInitialAmount, newOwner, isPool, dexAddress);
        emit TokenCreated(clone);
    }
}

